// Simple practice problem finder
const subject = $json.subject || 'math';
const topic = $json.topic || 'general';
const difficulty = $json.difficulty || 'medium';
const count = parseInt($json.problemCount) || 3;

const problems = [];

for (let i = 1; i <= count; i++) {
  problems.push({
    number: i,
    problem: `${subject.charAt(0).toUpperCase() + subject.slice(1)} problem ${i}: ${topic} (${difficulty})`,
    hints: [
      'Think about the core concept',
      'Break it down into steps',
      'Check your work'
    ],
    solution: `Worked solution for problem ${i} would be shown here.`,
    difficulty: difficulty
  });
}

return {
  json: {
    response: {
      subject: subject,
      topic: topic,
      difficulty: difficulty,
      totalProblems: count,
      problems: problems
    }
  }
};
