// Simple quiz generator
const topic = $json.topic || 'general';
const difficulty = $json.difficulty || 'medium';
const count = parseInt($json.questionCount) || 3;

const questions = [];

for (let i = 1; i <= count; i++) {
  questions.push({
    number: i,
    question: `Question ${i} about ${topic} (${difficulty} difficulty)`,
    type: 'multiple_choice',
    options: [
      'Option A',
      'Option B',
      'Option C',
      'Option D'
    ],
    correctAnswer: 'A',
    explanation: `This tests your understanding of ${topic}.`
  });
}

return {
  json: {
    response: {
      topic: topic,
      difficulty: difficulty,
      totalQuestions: count,
      questions: questions,
      instructions: `Answer all ${count} questions about ${topic}.`
    }
  }
};
